import { Component, OnInit } from '@angular/core';

@Component({
    selector: 'app-about',
    templateUrl: './about.component.html',
    styleUrls: ['./about.component.css']
})
export class AboutComponent implements OnInit {
    age: number;
    skills: string[];

    constructor() {
        let timeDiff = new Date().getTime() - new Date("2003-01-06").getTime();
        this.age = Math.floor((timeDiff / (1000 * 3600 * 24))/365);
        this.skills = [
            "Java",
            "Python",
            "Angular",
            "Spring Boot",
            "Docker",
            "Algorithms and Data Structures",
            "Object Oriented Programming",
            "REST APIs",
            "ViM",
        ];

    }

    ngOnInit(): void {
    }

}
<h1>Whoami</h1>

<div class="w-100 d-flex justify-content-center mt-5 flex-sm-row flex-column">
    <p class="col-md-3 links m-2">
        Hi, my name is Jip Derksen and I'm a {{ age }} year old software engineering student at the Amsterdam University
        of Applied Sciences.
    </p>
    <img src="../../../../assets/me.jpeg" class="image m-2" alt="picture of me">
</div>

<div class="d-flex mt-5 flex-column flex-sm-row">
    <div class="w-50">
        <h3>Coding Skills</h3>
    </div>
    <div class="w-sm-50 links">
        <ul>
            <li *ngFor="let skill of skills">
                <span class="material-icons align-bottom rotate">
                    rocket
                </span> 
                {{ skill }}
            </li>
        </ul>
    </div>
</div>
