import { Component, OnInit } from '@angular/core';

@Component({
    selector: 'app-contact',
    templateUrl: './contact.component.html',
    styleUrls: ['./contact.component.css']
})
export class ContactComponent implements OnInit {
    email: string;
    tel: string;
    linkedIn: string;
    insta: string;
    github: string;

    constructor() {
        this.email = "jip.derksen@hva.nl";
        this.tel = "+31 6 12345678";
        this.linkedIn = "https://www.linkedin.com/in/jipderksen";
        this.insta = "https://www.instagram.com/jipderksen";
        this.github = "https://www.github.com/CubedCard";
    }

    ngOnInit(): void {
    }

}
<h1>Contact</h1>
<br>
<br>
<div>
    <span class="material-icons align-bottom">
        <a href="mailto:{{ email }}">email</a>
    </span>
    <span class="material-icons align-bottom">
        <a href="{{ linkedIn }}" target="_blank"><img src="/assets/linkedIn.jpeg" class="white logo"></a>
    </span>
    <span class="material-icons align-bottom">
        <a href="tel:{{ tel }}">smartphone</a>
    </span>
    <span class="material-icons align-bottom">
        <a href="{{ insta }}" target="_blank"><img src="/assets/insta.png" class="logo"></a>
    </span>
    <span class="material-icons align-bottom">
        <a href="{{ github }}" target="_blank"><img src="/assets/git.png" class="logo white ms-2"></a>
    </span>
</div>
