import {Component, inject} from '@angular/core';
import {ActivatedRoute, NavigationEnd, Router, RouterOutlet} from '@angular/router';
import {Title} from '@angular/platform-browser';
import {HeaderComponent} from './components/header/header/header.component';
import {filter, map} from 'rxjs/operators';
import {takeUntilDestroyed} from '@angular/core/rxjs-interop';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [RouterOutlet, HeaderComponent],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Jip Derksen | ';

  private activatedRoute = inject(ActivatedRoute);
  private titleService = inject(Title);
  private router = inject(Router);

  constructor() {
    const appTitle = this.titleService.getTitle();
    this.router.events.pipe(
      filter(e => e instanceof NavigationEnd),
      map(() => {
        const child = this.activatedRoute.firstChild;
        const routeTitle = child?.snapshot.data?.['title'];
        return routeTitle ? this.title + routeTitle : appTitle;
      }),
      takeUntilDestroyed()
    ).subscribe(ttl => this.titleService.setTitle(ttl));
  }
}

<div class="d-flex w-100 h-100 flex-column">
    <app-header class="fixed-right m-3"></app-header>

    <main class="px-3 h-100 w-100 pb-5">
        <router-outlet></router-outlet>
    </main>

    <footer class="mt-auto text-white-50 fixed-bottom">
        <p>Cover template for <a href="https://getbootstrap.com/" class="text-white">Bootstrap</a>, by <a href="https://twitter.com/mdo" class="text-white">&#64;mdo</a>.</p>
    </footer>
</div>
